INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (97, 'Dialga', 'Il peut contrôler le temps. Les mythes de Sinnoh en parlentcomme d’une divinité ancienne.', 100,120,120,90,100,150,0, 'Dialga');
