INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (101, 'Giratina', 'Sa grande violence lui a valu d’être banni. Il observeles hommes en silence depuis le Monde Distorsion.', 150,100,120,90,120,100,0, 'Giratina');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (102, 'Cresselia', 'Dormir avec une de ses plumes à la main permet de fairede beaux rêves. On le surnomme « avatar du croissantde lune ».', 120,70,120,85,130,75,0, 'Cresselia');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (103, 'Phione', 'Ce Pokémon des mers chaudes revient toujours à son lieude naissance, peu importe la distance.', 80,80,80,80,80,80,0, 'Phione');
