INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (1, 'Tortipouss', 'Son corps produit de l’oxygène par photosynthèse. La feuille sur sa tête fletrit quand il a soif.', 55,68,64,31,55,45,false, 'Turtwig');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (2, 'Boskara', 'Il sait d’instinct où trouver une source d’eau pure. Il y transporte d’autres Pokemon sur son dos.', 75,89,85,36,65,55,false, 'Grotle');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (3, 'Torterra', 'Il arrive que de petits Pokemon se rassemblent sur son dos immobile pour y faire leur nid.', 95,109,105,56,85,75,false, 'Torterra');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (4, 'Ouisticram', 'La flamme de son posterieur brûle grâce à un gaz de son estomac. Elle faiblit quand il ne va pas bien.', 44,58,44,61,44,58,false, 'Chimchar');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (5, 'Chimpenfeu', 'Il attaque en prenant appui sur les murs et les plafonds. Sa queue ardente n’est pas son seul atout.', 64,78,52,81,52,78,false, 'Monferno');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (6, 'Simiabraz', 'Il fait voltiger ses ennemis grâce à sa vitesse et son style de combat special qui utilise ses quatre membres.', 76,104,71,108,71,104,false, 'Infernape');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (7, 'Tiplouf', 'Il est fier et deteste accepter la nourriture qu’on lui offre. Son pelage epais le protège du froid.', 53,51,53,40,56,61,false, 'Piplup');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (8, 'Prinplouf', 'C’est un Pokemon solitaire. Un seul coup de ses puissantes ailes peut briser un arbre en deux.', 64,66,68,50,76,81,false, 'Prinplup');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (9, 'Pingoleon', 'Les trois cornes de son bec sont le symbole de sa force. Celles du chef sont plus grosses que les autres.', 84,86,88,60,101,111,false, 'Empoleon');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (10, 'etourmi', 'Ce Pokemon très bruyant parcourt champs et forêts en nuees pour chasser les Pokemon Insecte.', 40,55,30,60,30,30,false, 'Starly');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (11, 'etourvol', 'Il peuple les champs et les forêts. Lorsque deux volees se croisent, elles luttent pour le territoire.', 55,75,50,80,40,40,false, 'Staravia');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (12, 'etouraptor', 'Lorsque etourvol evolue en etouraptor, il quitte son groupe pour vivre seul. Ses ailes sont très developpees.', 85,120,70,100,60,50,false, 'Staraptor');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (13, 'Keunotor', 'Il ronge constamment des troncs et des pierres pour se faire les incisives. Il niche le long de l’eau.', 59,45,40,31,40,35,false, 'Bidoof');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (14, 'Castorno', 'Il construit des barrages de boue et d’ecorce le long des fleuves. C’est un ouvrier de renom.', 79,85,60,71,60,55,false, 'Bibarel');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (15, 'Crikzik', 'Quand ses antennes s’entrechoquent, elles laissent echapperun bruit de xylophone.', 37,25,41,25,41,25,false, 'Kricketot');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (16, 'Melokrik', 'Il exprime ses emotions par des melodies. Les scientifiquesetudient actuellement leur structure.', 77,85,51,65,51,55,false, 'Kricketune');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (17, 'Lixy', 'Sa fourrure s’illumine grâce à l’electricite.Il agite l’extremite lumineuse de sa queuepour envoyer des signes à ses congenères.', 45,65,34,45,34,40,false, 'Shinx');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (18, 'Luxio', 'Quand il croise un ennemi, il se prepareau combat en sortant ses griffes dont les coupsont une puissance qui atteint un million de volts.', 60,85,49,60,49,60,false, 'Luxio');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (19, 'Luxray', 'Il dort beaucoup pour recuperer la quantited’energie electrique enorme qu’il consommeafin de voir à travers la matière.', 80,120,79,70,79,95,false, 'Luxray');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (20, 'Rozbouton', 'Il est sensible aux variations de temperature.Quand son bourgeon commence à s’ouvrir,l’arrivee du printemps est imminente.', 40,30,35,55,70,50,false, 'Budew');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (21, 'Roserade', 'Sa main gauche administre un poison lent,et la droite, un poison foudroyant.Les deux peuvent être fatals.', 60,70,65,90,105,125,false, 'Roserade');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (22, 'Kranidos', 'Son crâne extrêmement solide est sa principalecaracteristique. Il s’en servait pour faire tomberdes arbres les Baies dont il se nourrissait.', 67,125,40,58,30,30,false, 'Cranidos');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (23, 'Charkos', 'Pokemon prehistorique celèbre pour ses coupsde tête puissants et son petit cerveau, sonextinction serait due à son extrême bêtise.', 97,165,60,58,50,65,false, 'Rampardos');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (24, 'Dinoclier', 'On retrouve des fossiles de Dinoclier dansles couches sedimentaires prehistoriques, maisseule sa tête a resiste à l’epreuve du temps.', 30,42,118,30,88,42,false, 'Shieldon');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (25, 'Bastiodon', 'Un Pokemon eteint depuis environ 100 millionsd’annees. Sa tête incroyablement solide etaitplus dure que l’acier.', 60,52,168,30,138,47,false, 'Bastiodon');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (26, 'Cheniti', 'S’il perd sa cape au combat, il en tisse rapidementune nouvelle avec ce qui lui tombe sous la main.', 40,29,45,36,45,29,false, 'Burmy');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (27, 'Cheniselle', 'Quand Cheniti a evolue, sa cape a fusionne avec son corps.Cheniselle ne s’en separe jamais.', 60,59,85,36,105,79,false, 'Wormadam');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (28, 'Papilord', 'Ce Pokemon s’active à la nuit tombee pour voler le mieldes Apitrini pendant leur sommeil.', 70,94,50,66,50,94,false, 'Mothim');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (29, 'Apitrini', 'Du lever du soleil à la tombee de la nuit,il recolte du nectar pour Apireine et sa colonie.', 30,30,42,70,42,30,false, 'Combee');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (30, 'Apireine', 'Plus la quantite de pheromones qu’il libèreest elevee, plus le nombre d’Apitrini qu’il peutdiriger est grand.', 70,80,102,40,102,80,false, 'Vespiquen');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (31, 'Pachirisu', 'Il arrive que deux Pachirisu se frottent les joues pour partagerl’electricite qu’ils ont accumulee.', 60,45,70,95,90,45,false, 'Pachirisu');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (32, 'Mustebouee', 'Il utilise la bouee autour de son cou pour passer sa tête horsde l’eau et observer les alentours.', 55,65,35,85,30,60,false, 'Buizel');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (33, 'Musteflott', 'Il a developpe une bouee pour poursuivre ses proiesaquatiques. Elle fait office de canot gonflable.', 85,105,55,115,50,85,false, 'Floatzel');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (34, 'Ceribou', 'Plus son corps est rouge, plus la saveurde sa petite boule gorgee de nutrimentsest sucree et delicieuse.', 45,35,45,35,53,62,false, 'Cherubi');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (35, 'Ceriflor', 'Lorsqu’il se ferme, ses petales sont si durs qu’ilne craint plus les attaques des Pokemon oiseaux.', 70,60,70,85,78,87,false, 'Cherrim');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (36, 'Sancoki', 'Si on le caresse trop fort, il secrète un etrangeliquide mauve qui est inoffensif, mais dontla texture est très gluante.', 76,48,48,34,62,57,false, 'Shellos');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (37, 'Tritosor', 'Son corps est caoutchouteux, mais très resistant,car la puissance des chocs qu’il subit se repandsur toute sa surface.', 111,83,68,39,82,92,false, 'Gastrodon');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (38, 'Capidextre', 'Il utilise toujours ses deux queues pour fairela moindre tâche. Il s’en sert aussi pour enlacerles gens qu’il affectionne tout particulièrement.', 75,100,66,115,66,60,false, 'Ambipom');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (39, 'Baudrive', 'Nes d’une concentration d’âmes, ces Pokemonpeuvent apparaître en grand nombre les joursoù le taux d’humidite est eleve.', 90,50,34,70,44,60,false, 'Drifloon');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (40, 'Grodrive', 'Il attrape des personnes et des Pokemon pourles transporter vers une destination inconnue.', 150,80,44,80,54,90,false, 'Drifblim');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (41, 'Laporeille', 'Plus il s’entraîne, plus les coups qu’il assèneen deroulant vigoureusement ses oreillesgagnent en puissance.', 55,66,44,85,56,44,false, 'Buneary');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (42, 'Lockpin', 'À la fin de la saison chaude, il mue et se recouvred’une fourrure epaisse et legère pour se preparerà affronter le froid hivernal.', 65,76,84,105,96,54,false, 'Lopunny');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (43, 'Magirêve', 'Il susurre des maledictions pour tourmenterses ennemis et leur infliger de terriblesmigraines ou des hallucinations terrifiantes.', 60,60,60,105,105,105,false, 'Mismagius');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (44, 'Corboss', 'Ses sous-fifres font le sale boulot pour lui.Il n’intervient en combat que pour donnerle coup de grâce à son ennemi.', 100,125,52,71,52,105,false, 'Honchkrow');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (45, 'Chaglam', 'Lorsqu’il est heureux, sa queue decrit des arabesquescomme un ruban de gymnastique rythmique.', 49,55,42,85,37,42,false, 'Glameow');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (46, 'Chaffreux', 'Il ceinture sa taille de sa queue bifide pour paraîtreplus imposant.', 71,82,64,112,59,64,false, 'Purugly');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (47, 'Korillon', 'Quand il sautille, l’orbe qu’il a dans la bouche s’agite et tintecomme une cloche.', 45,30,50,45,50,65,false, 'Chingling');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (48, 'Moufouette', 'S’il lève la queue, c’est qu’il s’apprête à projeterun liquide tellement nauseabond qu’il peut faireperdre connaissance.', 63,63,47,74,41,41,false, 'Stunky');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (49, 'Moufflair', 'Ce Pokemon, qui creuse son terrier dans le sol,peut projeter un liquide à l’odeur infecte avecle bout de sa queue.', 103,93,67,84,61,71,false, 'Skuntank');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (50, 'Archeomire', 'On raconte qu’il se met à briller et à refleterla verite quand on le polit, mais il a horreurde cela.', 57,24,86,23,86,24,false, 'Bronzor');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (51, 'Archeodong', 'De nombreux savants pensent que ce Pokemonn’est pas originaire de Galar à cause des motifsqui ornent son corps.', 67,89,116,33,116,79,false, 'Bronzong');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (52, 'Manzaï', 'Il vit dans les zones rocheuses arides.Quand ses boules vertes sont sèches,elles se mettent à briller legèrement.', 50,80,95,10,45,10,false, 'Bonsly');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (53, 'Mime Jr.', 'Il considère les meilleurs danseurs parmiles M. Glaquette comme ses maîtreset s’evertue à mimer leurs mouvements.', 20,25,45,60,90,70,false, 'Mime');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (54, 'Ptiravi', 'Il prête volontiers son caillou rond à ceuxqu’il apprecie, mais eclate en sanglots etse met en colère si on ne le lui rend pas.', 100,5,5,30,65,15,false, 'Happiny');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (55, 'Pijako', 'On peut lui enseigner quelques mots. S’il s’agit d’un groupe,ils retiendront les mêmes phrases.', 76,65,45,91,42,92,false, 'Chatot');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (56, 'Spiritomb', 'Pour punir son comportement deplorable,un voyageur a utilise un mysterieux sortilègepour l’enchaîner à une Cle de Voûte.', 50,92,108,35,108,92,false, 'Spiritomb');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (57, 'Griknot', 'Il attaque et entraîne dans son antre tout ce quibouge, sans distinction. Sa gueule est immense,mais son estomac est relativement petit.', 58,70,45,42,45,40,false, 'Gible');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (58, 'Carmache', 'Il cache des pierres precieuses dans le solde sa grotte, et accueille à coups de griffeset de crocs quiconque ose y penetrer.', 68,90,65,82,55,50,false, 'Gabite');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (59, 'Carchacrok', 'Ce Pokemon très rapide même au sol captureses proies sur les monts enneiges et rentre vitedans son antre avant que son corps ne gèle.', 108,130,95,102,85,80,false, 'Garchomp');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (60, 'Goinfrex', 'Il conserve sa nourriture sous son epaisse fourrure.Il lui arrive d’en offrir un peu à ceux en quiil a confiance.', 135,85,40,5,85,40,false, 'Munchlax');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (61, 'Riolu', 'Il perçoit sous forme d’aura non seulementles sentiments des autres, mais aussi l’etatde la nature.', 40,70,40,60,40,35,false, 'Riolu');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (62, 'Lucario', 'etant capable de lire ce que pensent les humains,ce Pokemon ne s’attache qu’aux Dresseurs dotesd’un cœur pur.', 70,110,70,90,70,115,false, 'Lucario');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (63, 'Hippopotas', 'Il est principalement actif pendant la journee.Il s’enfouit profondement dans le sable pourdormir, car les nuits desertiques sont très froides.', 68,72,78,32,42,38,false, 'Hippopotas');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (64, 'Hippodocus', 'Il entre dans une violente colère quandon l’enerve : il expulse le sable qu’il a accumulepour provoquer de veritables tempêtes.', 108,112,118,47,72,68,false, 'Hippowdon');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (65, 'Rapion', 'Il attaque avec les pinces empoisonneesde sa queue. Le venin se repand alorsdans le corps de la victime et la paralyse.', 40,50,90,65,55,30,false, 'Skorupi');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (66, 'Drascore', 'Sa ferocite lui a valu le surnom de « demondu desert », mais face à un Hippodocus, il estparfaitement docile et evite de le provoquer.', 70,90,110,95,75,60,false, 'Drapion');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (67, 'Cradopaud', 'Dilue, son poison sert de remède. Cela lui a valude devenir la mascotte populaire d’une entreprisede produits pharmaceutiques.', 48,61,40,50,40,61,false, 'Croagunk');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (68, 'Coatox', 'Il lance un coassement viril de victoirequand il parvient à terrasser sa proie.Il s’apparente aux Crapustule.', 83,106,65,85,65,86,false, 'Toxicroak');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (69, 'Vortente', 'Il s’accroche aux arbres des marais et attire ses proies avecsa salive à l’odeur enivrante.', 74,100,72,46,72,90,false, 'Carnivine');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (70, 'ecayon', 'Il attire ses proies avec ses nageoires caudaleslumineuses. Il passe la journee à la surfaceet se retire dans les abysses la nuit venue.', 49,49,56,66,61,49,false, 'Finneon');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (71, 'Lumineon', 'Il se deplace en rampant dans les profondeursde l’ocean. Ses nageoires brillent telles desetoiles dans le ciel nocturne.', 69,69,76,91,86,69,false, 'Lumineon');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (72, 'Babimanta', 'Il nage au sein des bancs de Remoraid.Lorsqu’un predateur les attaque, il se batà leurs côtes.', 45,20,50,50,120,60,false, 'Mantyke');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (73, 'Blizzi', 'Les Baies en forme de crèmes glacees quipoussent sur son ventre sont le mets favorides Darumarond vivant à Galar.', 60,62,50,40,60,62,false, 'Snover');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (74, 'Blizzaroi', 'Ce grand Pokemon declenche des blizzards.Lorsqu’il agite son corps, tout ce qui l’entourese retrouve couvert de neige.', 90,92,75,60,85,92,false, 'Abomasnow');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (75, 'Dimoret', 'Il communique avec ses semblables en gravantdes motifs avec ses griffes. Il existerait prèsde 500 signes differents.', 70,120,65,125,85,45,false, 'Weavile');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (76, 'Magnezone', 'On pense qu’il a evolue après avoir ete exposeà un champ magnetique particulier qui a modifiela structure moleculaire de son corps.', 70,70,115,60,90,130,false, 'Magnezone');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (77, 'Coudlangue', 'Il est aussi habile avec sa langue que les êtreshumains le sont avec leurs mains. En revanche,il est maladroit quand il utilise ses doigts.', 110,85,95,50,95,80,false, 'Lickilicky');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (78, 'Rhinastoc', 'Il repousse les attaques avec ses Protecteurs etprofite du moment où l’adversaire est destabilisepour le transpercer avec sa vrille, dont il est fier.', 115,140,130,40,55,55,false, 'Rhyperior');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (79, 'Bouldeneu', 'Ses lianes poussent plus rapidement s’il vitsous un climat chaud. Quand elles deviennenttrop longues, il se les coupe.', 100,100,125,50,50,110,false, 'Tangrowth');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (80, 'elekable', 'La quantite d’electricite qu’il genère est enorme,même pour un Pokemon electrik. Il inflige defortes decharges du bout de ses queues.', 75,123,67,95,85,95,false, 'Electivire');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (81, 'Maganon', 'Au fil du temps, son corps est devenu similaireà un volcan, et un de ses organes ressembledesormais à une chambre magmatique.', 75,95,67,83,95,125,false, 'Magmortar');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (82, 'Togekiss', 'On dit que ce Pokemon dispense ses bienfaits,ce qui explique pourquoi il est representesur les porte-bonheurs depuis fort longtemps.', 85,50,95,80,115,120,false, 'Togekiss');
INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (83, 'Yanmega', 'Il peut voler avec aise tout en portant un adulte dans ses sixpattes. Les ailes de sa queue lui servent de balancier.', 86,76,86,95,56,116,false, 'Yanmega');
