INSERT INTO "pokemon_type" (id, name, description, hp, attack, defense, speed, defense_special, attack_special, is_legendary, english_name) VALUES (91, 'Noctunoir', 'Il devore l’âme de ses ennemis en lesengloutissant dans sa bouche ventrale.Il recrache ensuite leur enveloppe corporelle.', 45,100,135,45,135,65,false, 'Dusknoir');
